mapper.select:

select no, name 
  from users 
 where email=#{email } and password=#{password }


select no, name, email 
  from users 
 where email = #{email }


select no, name, email 
  from users 
 where no = #{no }

choose.when:

update users
   set name = #{name }, 
       gender = #{gender } 
 where no = #{no }

choose.otherwise:

update users 
   set name = #{name }, 
       password = #{password }, 
       gender = #{gender } 
 where no = #{no }
